
public class AutoBoxingUnboxing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//Autoboxing
		char ch = 'a';
		Character a = ch; 
		System.out.println(a);
		
		//Unboxing
		Character b = 'b';
		char c = b;
		System.out.println(c);
		
	}

}
