
public class LargestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 20, b = 40, c = 60;
		
		if(a > b && a > c)
		{
			System.out.println(a);
		}
		else if(b > c)
		{
			System.out.println(b);
		}
		else {
			System.out.println(c);
		}

	}

}
